import React, { useState, useEffect } from "react";
import "./WelcomeMessage.css"; // Import external styles
import alertSound from "../welcome Notes/sound/bell.mp3"; // Import the sound file

const WelcomeMessage = ({ message = "Welcome to my portfolio! 🙏", delay = 3000, duration = 5000 }) => {
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    const audio = new Audio(alertSound); // Create a new audio object

    const timer = setTimeout(() => {
      setShowMessage(true); // Show the message
      // Try to play the sound
      audio.play().catch((err) => console.error("Audio playback failed:", err));
    }, delay);

    const hideTimer = setTimeout(() => {
      setShowMessage(false); // Hide the message
    }, delay + duration);

    return () => {
      clearTimeout(timer);
      clearTimeout(hideTimer);
      audio.pause(); // Stop the sound if component unmounts
      audio.currentTime = 0; // Reset the sound playback position
    };
  }, [delay, duration]);

  if (!showMessage) return null;

  return (
    <div className="welcome-message">
      {message}
    </div>
  );
};

export default WelcomeMessage;
