import React, { useState, useEffect } from 'react';
import { FaGithub, FaLinkedin } from 'react-icons/fa';

const Footer = () => (
  <footer className="footer text-center py-3" style={{ backgroundColor: '#333', color: '#fff' }}>
    <div className="container">
      <p>&copy; 2024 Patrick. All rights reserved.</p>
      <div className="social-links">
        <a
          href="https://github.com/Patrick-MS-13"
          target="_blank"
          rel="noopener noreferrer"
          className="mx-2"
          style={{ color: '#fff' }}
        >
          <FaGithub size={24} />
        </a>
        <a
          href="https://www.linkedin.com/in/patrick-ms-a9a435231"
          target="_blank"
          rel="noopener noreferrer"
          className="mx-2"
          style={{ color: '#fff' }}
        >
          <FaLinkedin size={24} />
        </a>
      </div>
    </div>
    <BackToTopButton />
  </footer>
);

const BackToTopButton = () => {
  const [visible, setVisible] = useState(false);

  const handleScroll = () => {
    if (window.scrollY > 300) {
      setVisible(true);
    } else {
      setVisible(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    visible && (
      <button
        onClick={scrollToTop}
        className="btn btn-secondary"
        style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          borderRadius: '50%',
          width: '40px',
          height: '40px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        â†‘
      </button>
    )
  );
};

export default Footer;
