// components/OfflineHandler.js
import React, { useEffect, useState } from "react";
import "../style/offline.css"; // Create a CSS file for styles if needed

const OfflineHandler = ({ children }) => {
  const [isOnline, setIsOnline] = useState(navigator.onLine);

  useEffect(() => {
    const handleConnectionChange = () => {
      setIsOnline(navigator.onLine);
    };

    window.addEventListener("online", handleConnectionChange);
    window.addEventListener("offline", handleConnectionChange);

    return () => {
      window.removeEventListener("online", handleConnectionChange);
      window.removeEventListener("offline", handleConnectionChange);
    };
  }, []);

  if (!isOnline) {
    return (
      <div id="offline-loader" className="offline-loader text-center p-6">
        <div className="relative flex w-64 animate-pulse gap-2 p-4 mx-auto">
          <div className="h-12 w-12 rounded-full bg-slate-400"></div>
          <div className="flex-1">
            <div className="mb-1 h-5 w-3/5 rounded-lg bg-slate-400"></div>
            <div className="h-5 w-[90%] rounded-lg bg-slate-400"></div>
          </div>
          <div className="absolute bottom-5 right-0 h-4 w-4 rounded-full bg-slate-400"></div>
        </div>
        <p className="text-sm text-gray-500 mt-4">
          You are currently offline. Please reconnect to view the full portfolio.
        </p>
      </div>
    );
  }

  return <>{children}</>;
};

export default OfflineHandler;
