import React, { useState, useEffect } from "react";
import "./WelcomeMessage.css"; // Import external styles

const WelcomeMessage = ({
  message = "Welcome to my portfolio!",
  delay = 3000,
  duration = 5000,
  userName = "Guest", // Default userName
}) => {
  const [showMessage, setShowMessage] = useState(false);

  // Function to get greeting based on time of day
  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) {
      return "Good Morning";
    } else if (hour < 18) {
      return "Good Afternoon";
    } else {
      return "Good Evening";
    }
  };

  const [greeting, setGreeting] = useState(getGreeting());

  useEffect(() => {
    const timer = setTimeout(() => setShowMessage(true), delay); // Show after `delay` ms

    const hideTimer = setTimeout(() => setShowMessage(false), delay + duration); // Hide after `duration` ms

    return () => {
      clearTimeout(timer);
      clearTimeout(hideTimer);
    }; // Cleanup on unmount
  }, [delay, duration]);

  const handleCloseMessage = () => {
    setShowMessage(false); // Close the message when user clicks the "Close" button
  };

  if (!showMessage) return null;

  return (
    <div className="welcome-message">
      <h2>{`${greeting}, Mr./Ms. ${userName}!`}</h2>
      <p>{message}</p>
      <button onClick={handleCloseMessage} style={{ padding: "8px 16px", cursor: "pointer" }}>
        Close
      </button>
    </div>
  );
};

export default WelcomeMessage;
