import React, { useState, useEffect } from 'react';
import { FaGithub, FaLinkedin } from 'react-icons/fa';
import 'bootstrap/dist/css/bootstrap.ma.css'; // Make sure you are importing Bootstrap CSS

const Footer = () => (
  <footer className="footer text-center py-3 bg-dark text-white">
    <div className="container">
      <p>&copy; 2024 Patrick. All rights reserved.</p>
      <div className="social-links">
        <a
          href="https://github.com/Patrick-MS-13"
          target="_blank"
          rel="noopener noreferrer"
          className="mx-2 text-white"
        >
          <FaGithub size={24} />
        </a>
        <a
          href="https://www.linkedin.com/in/patrick-ms-a9a435231"
          target="_blank"
          rel="noopener noreferrer"
          className="mx-2 text-white"
        >
          <FaLinkedin size={24} />
        </a>
      </div>
    </div>
    <BackToTopButton />
  </footer>
);

const BackToTopButton = () => {
  const [visible, setVisible] = useState(false);

  const handleScroll = () => {
    if (window.scrollY > 300) {
      setVisible(true);
    } else {
      setVisible(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    visible && (
      <button
        onClick={scrollToTop}
        className="btn btn-secondary position-fixed bottom-0 end-0 m-4 rounded-circle d-flex align-items-center justify-content-center"
        style={{
          width: '40px',
          height: '40px',
          zIndex: '9999', // Ensure it's above other elements
        }}
      >
        â†‘
      </button>
    )
  );
};

export default Footer;
