import React, { useEffect, useState } from "react";
import "../Offline/offline.css"; // Optional custom styling

const OfflineHandler = ({ children, isDarkMode }) => {
  const [isOnline, setIsOnline] = useState(navigator.onLine);

  useEffect(() => {
    const updateStatus = () => setIsOnline(navigator.onLine);

    window.addEventListener("online", updateStatus);
    window.addEventListener("offline", updateStatus);

    return () => {
      window.removeEventListener("online", updateStatus);
      window.removeEventListener("offline", updateStatus);
    };
  }, []);

  if (!isOnline) {
    return (
      <div className={`offline-loader ${isDarkMode ? "bg-dark text-light" : "bg-light text-dark"}`}>
        <div className="relative flex w-64 animate-pulse gap-2 p-4 mx-auto">
          <div className="h-12 w-12 rounded-full bg-slate-400"></div>
          <div className="flex-1">
            <div className="mb-1 h-5 w-3/5 rounded-lg bg-slate-400"></div>
            <div className="h-5 w-[90%] rounded-lg bg-slate-400"></div>
          </div>
          <div className="absolute bottom-5 right-0 h-4 w-4 rounded-full bg-slate-400"></div>
        </div>
        <p className="text-sm text-gray-500 mt-4 text-center">
          You are currently offline. Please reconnect to view the full portfolio.
        </p>
      </div>
    );
  }

  return <>{children}</>;
};

export default OfflineHandler;
