import React, { useState, useEffect } from "react";
import "./WelcomeMessage.css"; // Import external styles

const WelcomeMessage = ({ message = "Welcome to my portfolio!", delay = 3000, duration = 5000 }) => {
  const [showMessage, setShowMessage] = useState(false);
  const [name, setName] = useState(""); // To hold the user's name
  const [isNameEntered, setIsNameEntered] = useState(false); // To check if name is entered

  useEffect(() => {
    const timer = setTimeout(() => setShowMessage(true), delay); // Show after `delay` ms
    const hideTimer = setTimeout(() => setShowMessage(false), delay + duration); // Hide after `duration` ms

    return () => {
      clearTimeout(timer);
      clearTimeout(hideTimer);
    };
  }, [delay, duration]);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleNameSubmit = (e) => {
    e.preventDefault();
    if (name) {
      setIsNameEntered(true);
    }
  };

  if (!showMessage) return null;

  return (
    <div className="welcome-message">
      {!isNameEntered ? (
        <div>
          <h3>Please enter your name:</h3>
          <form onSubmit={handleNameSubmit}>
            <input
              type="text"
              value={name}
              onChange={handleNameChange}
              placeholder="Enter your name"
              style={{ padding: "10px", fontSize: "16px", margin: "10px 0" }}
            />
            <button type="submit" style={{ padding: "10px", fontSize: "16px" }}>
              Submit
            </button>
          </form>
        </div>
      ) : (
        <div>{`Welcome, Mr./Ms. ${name}! ${message}`}</div>
      )}
    </div>
  );
};

export default WelcomeMessage;
