import React, { useState } from 'react';
import MyNavbar from './components/Navbar';
import About from './components/About';
import SkillsSection from './components/SkillsSection';
import Projects from './components/Projects';
import Contact from './components/Contact';
import Footer from './components/Footer';
import Cursor from './components/Cursor';
import './style/main.css'; 
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [isDarkMode, setIsDarkMode] = useState(false); // State for dark mode

  // Function to toggle dark mode
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <div className={isDarkMode ? "bg-dark text-light" : "bg-light text-dark"}>
      <Cursor /> {/* Custom cursor component */}
      <MyNavbar isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} /> {/* Navigation bar */}
      <About isDarkMode={isDarkMode} /> 
      <SkillsSection isDarkMode={isDarkMode} /> 
      <Projects isDarkMode={isDarkMode} /> 
      <Contact isDarkMode={isDarkMode} /> {/* Contact section */}
      <Footer isDarkMode={isDarkMode} /> {/* Footer with social links and back to top button */}
    </div>
  );
}

export default App;
