import React, { useState, useEffect } from "react";
import "./WelcomeMessage.css"; // Import external styles

const WelcomeMessage = ({ message = "Welcome to my portfolio!", delay = 3000, duration = 15000 }) => {
  const [showMessage, setShowMessage] = useState(false); // Controls visibility of the message
  const [name, setName] = useState(""); // To hold the user's name
  const [isNameEntered, setIsNameEntered] = useState(false); // To check if the name is entered
  const [inputTimeout, setInputTimeout] = useState(null); // Store timeout reference for inactivity

  const handleNameChange = (e) => {
    setName(e.target.value); // Update name on input change
    if (inputTimeout) {
      clearTimeout(inputTimeout); // Clear previous inactivity timeout
    }
    // Reset inactivity timeout if user starts typing
    setInputTimeout(setTimeout(() => {
      setShowMessage(true); // Show welcome message after 15 seconds of inactivity
    }, 300000)); // Set timeout to 15 seconds
  };

  const handleNameSubmit = (e) => {
    e.preventDefault();
    if (name) {
      setIsNameEntered(true); // If name is entered, show the welcome message with the name
    }
  };

  return (
    <div className="welcome-message">
      {!isNameEntered ? (
        <div>
          <h3>Please enter your name:</h3> {/* Prompt for name */}
          <form onSubmit={handleNameSubmit}>
            <input
              type="text"
              value={name}
              onChange={handleNameChange}
              placeholder="Enter your name"
              style={{ padding: "10px", fontSize: "16px", margin: "10px 0" }}
            />
            <button type="submit" style={{ padding: "10px", fontSize: "16px" }}>
              Submit
            </button>
          </form>
        </div>
      ) : (
        <div>{`Welcome, Mr.${name}! ${message}`}</div>
      )}
    </div>
  );
};

export default WelcomeMessage;
