import React, { useState, useEffect } from "react";
import "./WelcomeMessage.css"; // Import external styles

const WelcomeMessage = ({ message = "Welcome to my portfolio!", delay = 3000, duration = 5000 }) => {
  const [showMessage, setShowMessage] = useState(false); // Controls the visibility of the message
  const [name, setName] = useState(""); // To hold the user's name
  const [isNameEntered, setIsNameEntered] = useState(false); // To check if the name is entered
  const [isInputActive, setIsInputActive] = useState(false); // Track if the input field has mouse interaction
  const [timeoutReached, setTimeoutReached] = useState(false); // Track inactivity timeout
  const [inputTimeout, setInputTimeout] = useState(null); // Store timeout reference

  useEffect(() => {
    const timer = setTimeout(() => setShowMessage(true), delay); // Show after `delay` ms
    const hideTimer = setTimeout(() => setShowMessage(false), delay + duration); // Hide after `duration` ms

    return () => {
      clearTimeout(timer);
      clearTimeout(hideTimer);
    }; // Cleanup on unmount
  }, [delay, duration]);

  const handleNameChange = (e) => {
    setName(e.target.value);
    if (inputTimeout) {
      clearTimeout(inputTimeout); // Clear previous timeout if user starts typing again
    }
    // Reset inactivity timeout if there's user input
    const newTimeout = setTimeout(() => {
      setTimeoutReached(true); // If no interaction after 7 seconds, show the welcome message
    }, 7000);
    setInputTimeout(newTimeout);
    setIsInputActive(true); // Indicate input field has activity
  };

  const handleNameSubmit = (e) => {
    e.preventDefault();
    if (name) {
      setIsNameEntered(true);
    }
  };

  const handleInputFocus = () => {
    setIsInputActive(true); // Input field has focus
    if (inputTimeout) {
      clearTimeout(inputTimeout); // Clear the inactivity timeout when there's user interaction
    }
  };

  const handleInputBlur = () => {
    if (name === "") {
      setIsInputActive(false); // Input field has lost focus, but we need to track if the name is empty
    }
  };

  useEffect(() => {
    if (timeoutReached) {
      // If 7 seconds pass without interaction, show the welcome message
      setIsNameEntered(true); 
    }
  }, [timeoutReached]);

  return (
    <div className="welcome-message">
      {!isNameEntered ? (
        <div>
          {!isInputActive && !timeoutReached ? (
            <h3>Please enter your name:</h3> // Display message initially or if input is not interacted with
          ) : null}

          <form onSubmit={handleNameSubmit}>
            <input
              type="text"
              value={name}
              onChange={handleNameChange}
              placeholder="Enter your name"
              onFocus={handleInputFocus} // Trigger focus event
              onBlur={handleInputBlur} // Trigger blur event
              style={{ padding: "10px", fontSize: "16px", margin: "10px 0" }}
            />
            <button type="submit" style={{ padding: "10px", fontSize: "16px" }}>
              Submit
            </button>
          </form>
        </div>
      ) : (
        <div>{`Welcome, Mr./Ms. ${name}! ${message}`}</div> // Show welcome message after submission
      )}
    </div>
  );
};

export default WelcomeMessage;
