import React, { useState, useEffect } from 'react';
import './ChatBot.css';
import { FaPaperPlane, FaCommentDots } from 'react-icons/fa';

const ChatBot = () => {
    const [messages, setMessages] = useState([
        { from: 'bot', text: 'Hi there! Ask me anything about Patrick.' }
    ]);
    const [input, setInput] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const [darkMode, setDarkMode] = useState(false);
    const chatboxRef = React.useRef(null);
    const [showScrollDown, setShowScrollDown] = useState(false);

    const scrollToBottom = () => {
        const chatbox = chatboxRef.current;
        if (chatbox) {
            chatbox.scrollTo({
                top: chatbox.scrollHeight,
                behavior: 'smooth',
            });
        }
    };
    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    useEffect(() => {
        const chatbox = chatboxRef.current;
        if (!chatbox) return;

        const handleScroll = () => {
            const atBottom = chatbox.scrollHeight - chatbox.scrollTop <= chatbox.clientHeight + 10;
            setShowScrollDown(!atBottom);
        };

        chatbox.addEventListener('scroll', handleScroll);
        return () => chatbox.removeEventListener('scroll', handleScroll);
    }, []);


    const handleSend = () => {
        if (input.trim() === '') return;

        const newMessages = [...messages, { from: 'user', text: input }];
        setMessages(newMessages);
        setIsTyping(true);

        setTimeout(() => {
            const response = getBotResponse(input.toLowerCase());
            if (response) {
                setMessages([...newMessages, { from: 'bot', text: response }]);
            }
            setIsTyping(false);
        }, 700);

        setInput('');
    };

    // Then conditionally add class
    useEffect(() => {
        document.body.classList.toggle('dark-mode', darkMode);
    }, [darkMode]);


    const handleClear = () => {
        setMessages([{ from: 'bot', text: 'Chat cleared. Let‚Äôs start over!' }]);
    };

   const getBotResponse = (message) => {
    const worldTimeZones = {
        london: 'Europe/London',
        newyork: 'America/New_York',
        tokyo: 'Asia/Tokyo',
        sydney: 'Australia/Sydney',
        paris: 'Europe/Paris',
        dubai: 'Asia/Dubai',
        chennai: 'Asia/Kolkata',
        singapore: 'Asia/Singapore',
        toronto: 'America/Toronto',
        berlin: 'Europe/Berlin'
    };

    const mockWeatherData = {
        london: "üåßÔ∏è Light rain with a high of 14¬∞C",
        newyork: "‚õÖ Partly cloudy, 21¬∞C",
        tokyo: "üå§Ô∏è Mostly sunny, 25¬∞C",
        sydney: "‚òÄÔ∏è Sunny and warm, 27¬∞C",
        paris: "üå•Ô∏è Cloudy with a chance of showers, 16¬∞C",
        dubai: "üåû Hot and sunny, 35¬∞C",
        chennai: "üå¶Ô∏è Humid with occasional rain, 32¬∞C",
        singapore: "üåßÔ∏è Thunderstorms expected, 30¬∞C",
        toronto: "‚ùÑÔ∏è Snow flurries and cold, -2¬∞C",
        berlin: "üå´Ô∏è Foggy morning, clearing by noon, 10¬∞C"
    };

    const getTimeInCity = (city) => {
        const timeZone = worldTimeZones[city.toLowerCase()];
        if (timeZone) {
            const time = new Intl.DateTimeFormat('en-US', {
                hour: '2-digit',
                minute: '2-digit',
                second: '2-digit',
                timeZone,
            }).format(new Date());
            return `üïí The current time in ${city.charAt(0).toUpperCase() + city.slice(1)} is ${time}`;
        } else {
            return `üåç Sorry, I don‚Äôt know the time zone for "${city}". Try asking for time in London, Tokyo, NewYork, etc.`;
        }
    };

    const getWeatherInCity = (city) => {
        const weather = mockWeatherData[city.toLowerCase()];
        if (weather) {
            return `üå§Ô∏è Current weather in ${city.charAt(0).toUpperCase() + city.slice(1)}: ${weather}`;
        } else {
            return `üåç Sorry, I don‚Äôt have weather data for "${city}" yet. Try asking about London, Tokyo, or NewYork.`;
        }
    };

    // ‚Äî Chatbot Answers ‚Äî
    if (message.includes('name')) return "I'm Patrick's personal chatbot!";
    if (message.includes('skills') || message.includes('tech')) return "Patrick knows React, Python, SQL, and loves working with data!";
    if (message.includes('contact')) return "You can contact him through the Contact section of this portfolio!";
    if (message.includes('project')) return "Patrick has built cool projects with React, Data Visualizations, and Time Apps. Check out the Projects section!";
    if (message.includes('experience')) return "He has experience in frontend development and is currently exploring Data Analytics.";
    if (message.includes('hello') || message.includes('hi') || message.includes('hey')) return "Hey there! üëã How can I assist you?";

    // üåç World time
    if (message.match(/time in\s+\w+/)) {
        const city = message.split("time in")[1].trim();
        return getTimeInCity(city);
    }

    if (message.includes('world time')) {
        return "üåç Try asking 'time in Tokyo' or 'time in NewYork' to get the current time there.";
    }

    if (message.includes('time')) return `Right now it's ${new Date().toLocaleTimeString()}`;
    if (message.includes('date')) return `Today's date is ${new Date().toLocaleDateString()}`;

    // üå¶Ô∏è World weather
    if (message.match(/weather in\s+\w+/)) {
        const city = message.split("weather in")[1]?.trim();
        return getWeatherInCity(city);
    }

    if (message.includes('weather')) return "üå¶Ô∏è You can ask something like 'weather in Paris' or 'weather in Dubai'.";

    if (message.includes('news') || message.includes('latest news')) return "üì∞ Stay tuned! News integration is coming soon. Meanwhile, check your favorite news source for updates.";
    if (message.includes('education')) return "Patrick has a solid foundation in programming and continues to learn through hands-on projects and certifications.";
    if (message.includes('hobbies')) return "Patrick enjoys coding, exploring data, building cool web apps, and diving into new tech.";
    if (message.includes('goal') || message.includes('future')) return "Patrick aims to become a top-tier data analyst and continue building intelligent, user-focused applications.";

    if (message.includes('clear')) {
        handleClear();
        return '';
    }

    return "Hmm... I‚Äôm still learning. Try asking about skills, world weather, world time, or how to contact Patrick.";
};


    return (
        <>
            <div className={`chatbot-container ${isOpen ? 'open' : ''}`}>
                <div className="chatbox" ref={chatboxRef}>
                    {messages.map((msg, idx) => (
                        <div key={idx} className={`message ${msg.from}`}>
                            {msg.text}
                        </div>
                    ))}
                    {isTyping && <div className="message bot typing">Typing...</div>}

                    {showScrollDown && (
                        <div className="scroll-down-hint" onClick={scrollToBottom} title="Scroll to latest">
                            <svg fill="#ffffff" version="1.1" xmlns="http://www.w3.org/2000/svg" width="28px" height="28px" viewBox="-42 -42 184.00 184.00" stroke="#ffffff" strokeWidth="0.001">
                                <g>
                                    <path d="M50.5,19.881c-1.104,0-2,0.896-2,2V72.17L33.193,56.609c-0.781-0.781-1.922-0.781-2.703,0 
                    c-0.781,0.78-0.719,2.047,0.062,2.828l18.883,18.857c0.375,0.375,0.899,0.586,1.43,0.586s1.047-0.211,1.422-0.586l18.857-18.857 
                    c0.781-0.781,0.783-2.048,0.002-2.828c-0.781-0.781-2.296-0.781-3.077,0L52.5,71.933V21.881
                    C52.5,20.776,51.604,19.881,50.5,19.881z" />
                                </g>
                            </svg>
                        </div>
                    )}

                </div>

                <div className="input-area">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyDown={(e) => e.key === 'Enter' && handleSend()}
                        placeholder="Type your message..."
                        id="message-input"
                    />
                    <input
                        type="button"
                        onClick={handleSend}
                        className="send-btn"
                        id="send-btn"
                    />
                    <label htmlFor="send-btn" className="send-icon">
                        <FaPaperPlane />
                    </label>
                </div>

                <input
                    type="button"
                    value="Clear Chat"
                    className="btn btn-danger clear-btn"
                    onClick={handleClear}
                />
            </div>

            <button
                onClick={() => setIsOpen(!isOpen)}
                className="chat-toggle-btn"
                title="Open Chat"
            >
                <FaCommentDots />
            </button>
        </>
    );
};

export default ChatBot;
