import React, { useState } from 'react';
import './ChatBot.css';

const ChatBot = () => {
  const [messages, setMessages] = useState([
    { from: 'bot', text: 'Hi there! Ask me anything about Patrick.' }
  ]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim() === '') return;

    const newMessages = [...messages, { from: 'user', text: input }];

    const response = getBotResponse(input.toLowerCase());

    setMessages([...newMessages, { from: 'bot', text: response }]);
    setInput('');
  };

  const getBotResponse = (message) => {
    if (message.includes('name')) return "I'm Patrick's chatbot!";
    if (message.includes('skills')) return "Patrick knows React, Python, SQL, and loves Data Analytics!";
    if (message.includes('contact')) return "You can reach him via the Contact page!";
    return "I'm still learning! Try asking about skills, contact, or projects.";
  };

  return (
    <div className="chatbot-container">
      <div className="chatbox">
        {messages.map((msg, idx) => (
          <div key={idx} className={`message ${msg.from}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="input-area">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
          placeholder="Type your message..."
        />
        <button onClick={handleSend}>Send</button>
        <input
                type="submit"
                value="Send Message"
                className="btn btn-primary"
                style={{ width: "138px", height: "53px" }}
              />
      </div>
    </div>
  );
};

export default ChatBot;
