import React, { useState, useEffect } from "react";
import "./WelcomeMessage.css"; // Import external styles

const WelcomeMessage = ({
  message = "Welcome to my portfolio!",
  delay = 3000,
  duration = 5000,
  userName = "Guest", // Default to "Guest" if no userName is provided
}) => {
  const [showMessage, setShowMessage] = useState(false);
  const [soundPlayed, setSoundPlayed] = useState(false); // Track if sound has been played

  // Function to get greeting based on time of day
  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) {
      return "Good Morning";
    } else if (hour < 18) {
      return "Good Afternoon";
    } else {
      return "Good Evening";
    }
  };

  const [greeting, setGreeting] = useState(getGreeting());

  // Sound effect for showing and hiding the message
  const playSound = (action) => {
    // Delay audio play to make sure it's after user interaction
    if (!soundPlayed) {
      const sound = new Audio(action === "show" ? "/sound_show.mp3" : "/sound_hide.mp3"); // Sound file paths
      sound.play();
      setSoundPlayed(true); // Set sound played to true to ensure we play sound after interaction
    }
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowMessage(true);
      playSound("show");
    }, delay); // Show after `delay` ms

    const hideTimer = setTimeout(() => {
      setShowMessage(false);
      playSound("hide");
    }, delay + duration); // Hide after `duration` ms

    return () => {
      clearTimeout(timer);
      clearTimeout(hideTimer);
    }; // Cleanup on unmount
  }, [delay, duration, soundPlayed]);

  const handleCloseMessage = () => {
    setShowMessage(false); // Close the message when user clicks the "Close" button
    playSound("hide"); // Play hide sound when closing manually
  };

  if (!showMessage) return null;

  return (
    <div className="welcome-message">
      <h2>{`${greeting}, Mr./Ms. ${userName}!`}</h2> {/* Time-dependent greeting */}
      <p>{message}</p>
      <button onClick={handleCloseMessage} style={{ padding: "8px 16px", cursor: "pointer" }}>
        Close
      </button>
    </div>
  );
};

export default WelcomeMessage;
