import React, { useState } from 'react';
import './../style/contact.css'; // Import the CSS file for the Contact page

const Contact = ({ isDarkMode }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', name);
    formData.append('email', email);
    formData.append('message', message);

    try {
      const response = await fetch('https://formspree.io/f/mldenrna', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        setSubmitted(true);  // Set submitted state to true
        setError(false); // Reset error state
      } else {
        setError(true); // If response is not OK, show error
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      setError(true); // Show error in case of network issues
    }

    // Reset form fields after submission
    setName('');
    setEmail('');
    setMessage('');
  };

  return (
    <section id="contact" className={`contact-section py-5 ${isDarkMode ? 'bg-dark text-light' : 'bg-light text-dark'}`}>
      <div className="container">
        <h2 className="text-center mb-4">Contact Me</h2>
        {submitted && (
          <div className="alert alert-success text-center">
            Your message has been sent!
          </div>
        )}
        {error && (
          <div className="alert alert-danger text-center">
            Something went wrong. Please try again.
          </div>
        )}
        <form onSubmit={handleSubmit} className="mx-auto" style={{ maxWidth: '600px' }}>
          <div className="form-group mb-3">
            <input
              type="text"
              name="name"
              className="form-control"
              placeholder="Your Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="form-group mb-3">
            <input
              type="email"
              name="email"
              className="form-control"
              placeholder="Your Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group mb-3">
            <textarea
              className="form-control"
              name="message"
              rows="4"
              placeholder="Your Message"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
            ></textarea>
          </div>
          <div className="text-center">
            <button type="submit" className="btn btn-primary">Send Message</button>
          </div>
        </form>
      </div>
    </section>
  );
};

export default Contact;
