import React, { useState, useEffect } from 'react';
import './ChatBot.css';

const ChatBot = () => {
    const [messages, setMessages] = useState([
        { from: 'bot', text: 'Hi there! Ask me anything about Patrick.' }
    ]);
    const [input, setInput] = useState('');
    const [isTyping, setIsTyping] = useState(false);

    const handleSend = () => {
        if (input.trim() === '') return;

        const newMessages = [...messages, { from: 'user', text: input }];
        setMessages(newMessages);
        setIsTyping(true);

        setTimeout(() => {
            const response = getBotResponse(input.toLowerCase());
            setMessages([...newMessages, { from: 'bot', text: response }]);
            setIsTyping(false);
        }, 700); // Simulates bot typing delay

        setInput('');
    };

    const getBotResponse = (message) => {
        if (message.includes('name')) return "I'm Patrick's personal chatbot!";
        if (message.includes('skills') || message.includes('tech')) return "Patrick knows React, Python, SQL, and loves working with data!";
        if (message.includes('contact')) return "You can contact him through the Contact section of this portfolio!";
        if (message.includes('project')) return "Patrick has built cool projects with React, Data Visualizations, and Time Apps. Check out the Projects section!";
        if (message.includes('experience')) return "He has experience in frontend development and is currently exploring Data Analytics.";
        if (message.includes('hello') || message.includes('hi') || message.includes('hey')) return "Hey there! ðŸ‘‹ How can I assist you?";
        if (message.includes('time')) return `Right now it's ${new Date().toLocaleTimeString()}`;
        if (message.includes('date')) return `Today's date is ${new Date().toLocaleDateString()}`;
        if (message.includes('clear')) {
            setMessages([{ from: 'bot', text: 'Chat cleared. Letâ€™s start over!' }]);
            return '';
        }

        return "Hmm... Iâ€™m still learning. Try asking about skills, projects, experience, or how to contact Patrick.";
    };

    const handleClear = () => {
        setMessages([{ from: 'bot', text: 'Chat cleared. Letâ€™s start over!' }]);
    };

    return (
        <div className="chatbot-container">
            <div className="chatbox">
                {messages.map((msg, idx) => (
                    <div key={idx} className={`message ${msg.from}`}>
                        {msg.text}
                    </div>
                ))}
                {isTyping && <div className="message bot typing">Typing...</div>}
            </div>

            <div className="input-area">
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && handleSend()}
                    placeholder="Type your message..."
                />
                <input
                    onClick={handleSend}
                    type="submit"
                    value="Send Message"
                    className="btn btn-primary"
                    style={{ width: "138px", height: "53px" }}
                />
            </div>

            <input
                className="btn btn-danger clear-btn"
                onClick={handleClear}
                type="button"
                value="Clear Chat"
            />

        </div>
    );
};

export default ChatBot;
