// src/components/ChatBot.jsx
import React, { useState } from 'react';
import axios from 'axios';
import './ChatBot.css'; // optional for styling

const ChatBot = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);

    const getBotReply = async (userMessage) => {
        setLoading(true);
        try {
            const response = await axios.post(
                'https://api.openai.com/v1/chat/completions',
                {
                    model: 'gpt-3.5-turbo',
                    messages: [
                        { role: 'system', content: "You are Patrick's portfolio assistant." },
                        { role: 'user', content: userMessage }
                    ]
                },
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
                    }
                }
            );
            return response.data.choices[0].message.content.trim();
        } catch (error) {
            console.error('OpenAI Error:', error);
            return "Sorry, something went wrong.";
        } finally {
            setLoading(false);
        }
    };

    const handleSend = async () => {
        if (!input.trim()) return;

        const newMessages = [...messages, { from: 'user', text: input }];
        setMessages(newMessages);

        const reply = await getBotReply(input);
        setMessages([...newMessages, { from: 'bot', text: reply }]);

        setInput('');
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSend();
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                {messages.map((msg, idx) => (
                    <div key={idx} className={`chat-bubble ${msg.from}`}>
                        {msg.text}
                    </div>
                ))}
                {loading && <div className="chat-bubble bot">Typing...</div>}
            </div>
            <div className="chat-input-area">
                <input
                    type="text"
                    placeholder="Type your message..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={handleKeyPress}
                />
                <button onClick={handleSend}>Send</button>
            </div>
        </div>
    );
};

export default ChatBot;
