import React, { useState, useEffect } from "react";
import "./WelcomeMessage.css";
import alertSound from "../welcome Notes/sound/bell.mp3";

const WelcomeMessage = ({ message = "Welcome to my portfolio! ðŸ™", delay = 3000, duration = 5000 }) => {
  const [showMessage, setShowMessage] = useState(false);
  const [audio] = useState(new Audio(alertSound)); // Create the audio object only once

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowMessage(true);
      // Play sound when message appears
      audio.play().catch((err) => console.error("Audio playback failed:", err));
    }, delay);

    const hideTimer = setTimeout(() => {
      setShowMessage(false); // Hide the message after `duration`
    }, delay + duration);

    return () => {
      clearTimeout(timer);
      clearTimeout(hideTimer);
    };
  }, [delay, duration, audio]);

  // Request user interaction
  const handleUserInteraction = () => {
    audio.play().catch((err) => console.error("Audio playback failed:", err));
  };

  if (!showMessage) {
    return (
      <div className="splash-screen" onClick={handleUserInteraction}>
        <h1>Click to Enter</h1>
      </div>
    );
  }

  return <div className="welcome-message">{message}</div>;
};

export default WelcomeMessage;
