import React, { useEffect, useState } from 'react';
import '../style/main.css'; // Make sure this path is correct

const Cursor = () => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    const cursor = document.querySelector('.cursor');

    const moveCursor = (e) => {
      if (cursor) {
        const x = e.clientX;
        const y = e.clientY;
        cursor.style.left = `${x}px`;
        cursor.style.top = `${y + window.scrollY}px`; // Account for scrolling
      }
    };

    const handleHover = (e) => {
      const target = e.target.closest('.hover-effect');
      if (cursor) {
        if (
          ['H1', 'H2', 'H3', 'BUTTON', 'A'].includes(e.target.tagName) || target
        ) {
          const { width, height } = target ? target.getBoundingClientRect() : e.target.getBoundingClientRect();
          cursor.style.width = `${width}px`;
          cursor.style.height = `${height}px`;
          cursor.style.borderRadius = '8px';
          cursor.style.transform = 'translate(-50%, -50%) scale(1.2)';
        } else {
          cursor.style.width = '30px';
          cursor.style.height = '30px';
          cursor.style.borderRadius = '50%';
          cursor.style.transform = 'translate(-50%, -50%) scale(1)';
        }
      }
    };

    // Add event listeners for desktop view
    if (!isMobile) {
      document.addEventListener('mousemove', moveCursor);
      document.addEventListener('mouseover', handleHover);
      document.addEventListener('mouseout', handleHover);
    }

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      document.removeEventListener('mousemove', moveCursor);
      document.removeEventListener('mouseover', handleHover);
      document.removeEventListener('mouseout', handleHover);
    };
  }, [isMobile]);

  if (isMobile) return null; // Do not render cursor on mobile

  return <div className="cursor"></div>;
};

export default Cursor;
