import React, { useEffect } from 'react';
import '../style/main.css'; // Ensure this path is correct

const Cursor = () => {
  useEffect(() => {
    const cursor = document.querySelector('.cursor');

    // Move cursor with mouse movement
    const moveCursor = (e) => {
      if (cursor) {
        const x = e.clientX;
        const y = e.clientY;
        cursor.style.left = `${x}px`;
        cursor.style.top = `${y + window.scrollY}px`; // Account for scrolling
      }
    };

    // Change cursor style on hover over specific text or button elements
    const handleHover = (e) => {
      const target = e.target.closest('.hover-effect'); // Check for the hover-effect class
      if (cursor) {
        if (
          e.target.tagName === 'H1' ||
          e.target.tagName === 'H2' ||
          e.target.tagName === 'H3' ||
          e.target.tagName === 'BUTTON' ||
          e.target.tagName === 'A' || // Include links
          target
        ) {
          const { width, height } = target ? target.getBoundingClientRect() : e.target.getBoundingClientRect(); 
          cursor.style.width = `${width}px`;  
          cursor.style.height = `${height}px`; 
          cursor.style.borderRadius = '8px'; 
          cursor.style.transform = `translate(-50%, -50%) scale(1.2)`; // Scale up for a more interactive effect
        } else {
          cursor.style.width = '30px'; // Reset to original size
          cursor.style.height = '30px'; 
          cursor.style.borderRadius = '50%'; 
          cursor.style.transform = `translate(-50%, -50%) scale(1)`; // Reset to original shape
        }
      }
    };

    // Add event listeners
    document.addEventListener('mousemove', moveCursor);
    document.addEventListener('mouseover', handleHover);
    document.addEventListener('mouseout', handleHover);

    // Cleanup on component unmount
    return () => {
      document.removeEventListener('mousemove', moveCursor);
      document.removeEventListener('mouseover', handleHover);
      document.removeEventListener('mouseout', handleHover);
    };
  }, []);

  return <div className="cursor"></div>; // Render the cursor
};

export default Cursor;
