import React, { useState, useEffect } from "react";
import "./WelcomeMessage.css"; // Import external styles
import welcomebell from "./sound/bell.wav"; // Import sound file

const WelcomeMessage = ({ message = "Welcome to my portfolio!", delay = 3000, duration = 5000 }) => {
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    // Play sound when the message appears
    const sound = new Audio(welcomebell);

    const timer = setTimeout(() => {
      setShowMessage(true);
      sound.play(); // Play sound when message appears
    }, delay); // Show after `delay` ms

    const hideTimer = setTimeout(() => {
      setShowMessage(false);
    }, delay + duration); // Hide after `duration` ms

    return () => {
      clearTimeout(timer);
      clearTimeout(hideTimer);
    }; // Cleanup on unmount
  }, [delay, duration]);

  if (!showMessage) return null;

  return (
    <div className="welcome-message">
      {message}
    </div>
  );
};

export default WelcomeMessage;
